package week1.day2;

public class ChechIfNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num = 35;
if(num>0) {
System.out.println("the given number is positive:"+num);
	}
else if(num<0) {
	System.out.println("the given number is negative:"+num);
	
}
else if(num==0) {
	System.out.println("the given number is neither positive or negative:"+num);
}
}
}